import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/FlightBookings.css'; // (Optional: create or update this file for styling)

const FlightBookings = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [bookings, setBookings] = useState([]);
  const username = localStorage.getItem('username');

  useEffect(() => {
    fetchUserDetails();
    fetchBookings();
  }, []);

  const fetchUserDetails = async () => {
    try {
      const id = localStorage.getItem('userId');
      const response = await axios.get(`http://localhost:6001/fetch-user/${id}`);
      setUserDetails(response.data);
    } catch (error) {
      console.error('Failed to fetch user data', error);
    }
  };

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (error) {
      console.error('Failed to fetch bookings', error);
    }
  };

  const cancelTicket = async (id) => {
    try {
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert('Ticket cancelled successfully!');
      fetchBookings();
    } catch (error) {
      alert('Cancellation failed.');
    }
  };

  const renderApprovalMessage = () => {
    if (!userDetails) return null;

    if (userDetails.approval === 'not-approved') {
      return (
        <div className="approval-status-box pending">
          <h3>Awaiting Approval</h3>
          <p>Your request is under review by the admin team. Please check back soon.</p>
        </div>
      );
    }

    if (userDetails.approval !== 'approved') return null;

    return (
      <>
        <h2 className="bookings-title">Your Flight Bookings</h2>
        <div className="flight-bookings-list">
          {bookings.filter(b => b.flightName === username).map((booking) => (
            <div className="booking-card" key={booking._id}>
              <p><strong>Booking ID:</strong> {booking._id}</p>
              <div className="booking-info-grid">
                <div>
                  <p><strong>Flight ID:</strong> {booking.flightId}</p>
                  <p><strong>Flight Name:</strong> {booking.flightName}</p>
                </div>
                <div>
                  <p><strong>From:</strong> {booking.departure}</p>
                  <p><strong>To:</strong> {booking.destination}</p>
                </div>
                <div>
                  <p><strong>Date:</strong> {booking.journeyDate.slice(0, 10)}</p>
                  <p><strong>Time:</strong> {booking.journeyTime}</p>
                </div>
                <div>
                  <p><strong>Price:</strong> ₹{booking.totalPrice}</p>
                  <p><strong>Status:</strong> <span style={{ color: booking.bookingStatus === 'cancelled' ? 'red' : 'green' }}>{booking.bookingStatus}</span></p>
                </div>
              </div>
              <div>
                <p><strong>Passengers:</strong></p>
                <ul>
                  {booking.passengers.map((p, i) => (
                    <li key={i}>{p.name}, Age: {p.age}</li>
                  ))}
                </ul>
              </div>
              {booking.bookingStatus === 'confirmed' && (
                <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>
                  Cancel Booking
                </button>
              )}
            </div>
          ))}
        </div>
      </>
    );
  };

  return (
    <div className="flight-bookings-page">
      {renderApprovalMessage()}
    </div>
  );
};

export default FlightBookings;
